import {
    Button,
    TextField,
    MenuItem
} from '@material-ui/core';
import { useEffect } from 'react';
import {
    container as style_container,
    table as style_table,
    content as style_content
} from './index.module.css';

const Toolbox = ({
    year,
    type,
    search,
    button
}) => {
    let newData = [];
    useEffect(() => {
        if(year && year.data) {
            newData = JSON.stringify(year.data);
            newData.unshift({
                value: "Lütfen seçim yapın."
            });
        }
    });
    return <div
        className={style_container}
    >
        <div className={style_table}>
            <div className={style_content}>
                {
                    newData && newData.length ?
                        <TextField
                            id={year.id}
                            label={year.title}
                            select
                        >
                            {
                                newData.map((item, index) => {
                                    return <MenuItem
                                        key={year.key+index}
                                        value={item.value}
                                        {...item.props}
                                        
                                    >
                                        {item.value}
                                    </MenuItem>
                                })
                            }
                        </TextField>
                    :
                        null
                }
                {
                    type && type.data ?
                        <DropdownButton id={type.id} title={type.title}>
                            {
                                type.data.map((item, index) => {
                                    return <Dropdown.Item key={type.key+index}>{item.value}</Dropdown.Item>
                                })
                            }
                        </DropdownButton>
                    :
                        null
                }
                {
                    search ?
                        <input
                            type="text"
                            value={search.value}
                            onChange={search.onChange}
                            {...search.props}
                        />
                    :
                        null
                }
                {
                    button ?
                        <Button
                            {...button}
                        />
                    :
                        null
                }
            </div>
        </div>
    </div>
};
export default Toolbox;
