import {
    createContext,
    useContext,
    useReducer
} from 'react';

// Bu yöntem normalde tüm state'i eskisi gibi yönettiği için hook'lara göre sağlıksız.
// Fakat elimde örneği olduğu için hızlıca kullandım.

const defaultGlobalState = {
    myFavorites: [],
    setFavoriteState: () => {}
};

const globalStateContext = createContext(defaultGlobalState);
const dispatchStateContext = createContext(undefined);

const GlobalStateProvider = ({ children }) => {
    const [state, dispatch] = useReducer(
      (state, newValue) => ({ ...state, ...newValue }),
      defaultGlobalState
    );
    const myFavorites = JSON.parse(window.localStorage.getItem('myFavorites'));
    setGlobalState({
        ...globalState,
        myFavorites,
        setFavoriteState: (imdbID) => {
            if(JSON.parse(localStorage.getItem("myFavorites"))) {
                let myFavorites = JSON.parse(localStorage.getItem('myFavorites'));
                if(myFavorites.findIndex(e => e === imdbID) === -1) {
                    myFavorites.push(imdbID);
                } else {
                    myFavorites = myFavorites.filter(e => e !== imdbID);
                }
                localStorage.setItem("myFavorites", JSON.stringify(myFavorites));
                setGlobalState({
                    ...globalState,
                    myFavorites: myFavorites
                });
            } else {
                localStorage.setItem("myFavorites", JSON.stringify([imdbID]));
                setGlobalState({
                    ...globalState,
                    myFavorites: myFavorites
                });
            }
        }
    });
    return (
      <globalStateContext.Provider value={state}>
        <dispatchStateContext.Provider value={dispatch}>
          {children}
        </dispatchStateContext.Provider>
      </globalStateContext.Provider>
    );
};

export const useGlobalState = () => [
    useContext(globalStateContext),
    useContext(dispatchStateContext)
];

export default GlobalStateProvider;
