const defaultConfigs = {
    "method": "GET",
    "headers": {
        "Content-Type": "application/json",
        "Accept": "application/json"
    }
};
const api = (url, parameters, method, configs) => {
    let _configs = configs ? { ...configs, method, body: JSON.stringify(parameters) } : defaultConfigs;
    console.log(_configs);
    return new Promise((resolve, reject) => {
        fetch(url, _configs)
        .then((res) => {
            return res.json();
        })
        .then((res) => {
            resolve(res);
        })
        .catch((err) => {
            reject(err);
        });
    });
};
export default api;
