import {
    useEffect,
    useState
} from "react";
import Layout from "../../layout";
import Breadcrumbs from '../../components/breadcrumbs';
import Toolbox from "../../components/toolbox";

const Search = ({

}) => {
    const [path, setPath] = useState("/");
    const [searchText, setSearchText] = useState("");
    const [toolBoxSearchValue, setToolBoxSearchValue] = useState("");
    useEffect(() => {
        setPath(window.location.pathname);
        const _searchText = window.location.search.replace("?s=", "");
        setSearchText(_searchText);
    }, []);
    useEffect(() => {
        setPath(window.location.pathname);
    });
    return <Layout>
        <Breadcrumbs
            data={[
                {
                    title: "Home",
                    url: "/"
                },
                {
                    title: "Search results for " + searchText.toString(),
                    selected: true
                }
            ]}
            path={path}
        />
        <div
            style={{
                maxWidth: "100%",
                display: "table",
                position: "relative"
            }}
        >
            <Toolbox
                button={{
                    label: "Search",
                    props: {
                        variant: "contained",
                        color: "primary"
                    }
                }}
                search={{
                    value: toolBoxSearchValue,
                    onChange: (e) => setToolBoxSearchValue(e.target.value)
                }}
                year={{
                    key: "toolboxYear",
                    id: "dropdown-item-button",
                    title: "Year",
                    data: [
                        {
                            value: "Any",
                            default: true
                        },
                        {
                            value: "2019"
                        },
                        {
                            value: "2020"
                        }
                    ]
                }}
                type={{
                    key: "toolboxType",
                    id: "dropdown-item-button",
                    title: "Type",
                    data: [
                        {
                            value: "Any",
                            default: true
                        },
                        {
                            value: "Series"
                        },
                        {
                            value: "Movie"
                        },
                        {
                            value: "Episode"
                        }
                    ]
                }}
            />
        </div>
    </Layout>
};
export default Search;
