import {
    useReducer
} from 'react';
import {
    createStore
} from 'redux';

const initialState = {
    myFavorites: [],
    setFavoriteState: () => {}
};

const reducer = (state = initialState, action) => {
    return state;
}

const store = createStore(reducer);

export default store;
