import {
    useEffect,
    useState
} from "react";
import Layout from "../../layout";
import Breadcrumbs from '../../components/breadcrumbs';
import Toolbox from "../../components/toolbox";
import {
    title as style_title
} from './index.module.css';
import {
    API_KEY,
    HOST
} from "../../constants";
import {
    asyncForEach
} from "../../helpers";
import api from "../../server";

const Search = ({

}) => {
    const [path, setPath] = useState("/");
    const [searchText, setSearchText] = useState("");
    const [toolBoxSearchValue, setToolBoxSearchValue] = useState("");
    const [err, setErr] = useState(null);
    const [searchedFilms, setSearchedFilms] = useState([]);
    useEffect(() => {
        setPath(window.location.pathname);
        const _searchText = window.location.search.replace("?s=", "");
        setSearchText(_searchText.replace(/%20/g, " "));
        const mainParameters = {
			apikey: API_KEY,
			type: 'movie',
			r: 'json',
			s: searchText
		};
		const mainQuery = new URLSearchParams(mainParameters);
		api({
			url: HOST + '?' + mainQuery
		})
		.then(async (res) => {
			if (res.Response === 'True') {
				let fullyData = [];
				await asyncForEach(res.Search, async (item) => {
					const itemParameters = {
						apikey: API_KEY,
						plot: 'short',
						type: 'movie',
						r: 'json',
						i: item.imdbID
					}
					const itemQuery = new URLSearchParams(itemParameters);
					const response = await api({
						url: HOST + '?' + itemQuery
					});
                    let itemData = JSON.parse(JSON.stringify(response));
                    console.log(fullyData);
					fullyData.push(itemData);
				});
				setSearchedFilms(fullyData);
			} else {
				setErr({
					message: res['Error']
				});
			}
		})
		.catch((err) => {
			alert(err.message);
		});
    }, []);
    useEffect(() => {
        setPath(window.location.pathname);
    });
    return <Layout>
        <Breadcrumbs
            data={[
                {
                    title: "Home",
                    url: "/"
                },
                {
                    title: "Search results for " + searchText.toString(),
                    selected: true
                }
            ]}
            path={path}
        />
        <div
            style={{
                width: "100%",
                display: "table",
                marginTop: 20,
                marginBottom: 40
            }}
        >
            <div
                style={{
                    display: "table-cell"
                }}
            >
                <div
                    style={{
                        display: "flex",
                        paddingLeft: "10%",
                        paddingRight: "10%",
                    }}
                >
                    <Toolbox
                        position="relative"
                        button={{
                            label: "Search",
                            props: {
                                variant: "contained",
                                color: "primary"
                            }
                        }}
                        search={{
                            value: toolBoxSearchValue,
                            onChange: (e) => setToolBoxSearchValue(e.target.value)
                        }}
                        year={{
                            key: "toolboxYear",
                            id: "dropdown-item-button",
                            title: "Year",
                            data: [
                                {
                                    value: "Any",
                                    default: true
                                },
                                {
                                    value: "2019"
                                },
                                {
                                    value: "2020"
                                }
                            ]
                        }}
                        type={{
                            key: "toolboxType",
                            id: "dropdown-item-button",
                            title: "Type",
                            data: [
                                {
                                    value: "Any",
                                    default: true
                                },
                                {
                                    value: "Series"
                                },
                                {
                                    value: "Movie"
                                },
                                {
                                    value: "Episode"
                                }
                            ]
                        }}
                    />
                </div>
            </div>
        </div>
        <div
            style={{
                paddingLeft: "10%"
            }}
        >
            <h2 className={style_title}>Search result for <span style={{ color: "goldenrod" }}>{searchText}</span></h2>
            {
                searchedFilms.map((film, filmIndex) => {
                    return <FilmCard
                        imdbID={film.imdbID}
                        index={filmIndex}
                        title={film.Title}
                        image={film.Poster}
                        content={film.Plot}
                        imdbPoint={film.imdbRating}
                        key={"film-"+filmIndex}
                        year={film.Year}
                    />
                })
            }
        </div>
    </Layout>
};
export default Search;
