import { useEffect, useState } from 'react';
import Head from 'next/head';
import api from '../server';
import { API_KEY, HOST } from '../constants';
import { container as style_container } from './index.module.css';
import Layout from '../layout';
import Welcome from '../components/welcome';

export default function Home() {
	const [ films, setFilms ] = useState([]);
	const [ err, setErr ] = useState(null);
	useEffect(() => {
		const parameters = {
			apikey: API_KEY,
			s: 'Yor'
		};
		const query = new URLSearchParams(parameters);
		api({
			url: HOST + '?' + query
		})
			.then((res) => {
				console.log(res);
				if (res.Response === 'True') {
					setFilms(res.Search);
				} else {
					setErr({
						message: res['Error']
					});
				}
			})
			.catch((err) => {
				alert(err.message);
			});
	}, []);
	return <Layout>
		<div className={style_container}>
			<Head>
				<title>Create Next App</title>
				<link rel="icon" href="/favicon.ico" />
        <meta name="viewport" content="width=device-width, initial-scale=1.0"/>
			</Head>

			<main>
        <Welcome
          title="Welcome to MovieUP."
          content="Lorem ipsum dolor sit amet, consectetur adipiscing elit, sed do eiusmod tempor incididunt ut labore et dolore magna aliqua. Ut enim ad minim veniam, quis nostrud exercitation ullamco laboris "
        />
				{films && films.length ? (
					films.map((film, filmIndex) => {
						console.log(film);
						return (
							<div key={'films-' + filmIndex} className="container">
								<span className="postTitle">{film.Title}</span>
								<p className="" />
							</div>
						);
					})
				) : err ? (
					err.message
				) : (
					'Yükleniyor...'
				)}
			</main>
		</div>
  </Layout>
}
