import {
    useReducer
} from 'react';
import {
    createStore
} from 'redux';

const initialState = {
    myFavorites: [],
    setFavoriteState: () => {}
};

const reducer = () => {
    const [state, dispatch] = useReducer(
        (state, newValue) => ({ ...state, ...newValue }),
        initialState
    );
    return {
        state,
        dispatch
    };
}

const store = createStore(reducer);

export default store;
