import Link from 'next/link';
import {
    container as style_container
} from './index.module.css';

const Breadcrumbs = ({
    data,
    path
}) => {
    return <div
        className={style_container}
    >
        {
            data.map((item, index) => {
                return <Link
                    key={"breadcrumb"+index}
                    href={item.url}
                    style={{
                        color: path === item.url ? "goldenrod" : "black"
                    }}
                >
                    {item.title}
                    {
                        data.length !== index+1 ?
                            <span
                                style={{
                                    marginLeft: 8
                                }}
                            >
                                /
                            </span>
                        :
                            null
                    }
                </Link>
            })
        }
    </div>
};
export default Breadcrumbs;
