import {
    useReducer
} from 'react';
import {
    createStore
} from 'redux';

const initialState = {
    myFavorites: [],
    setFavoriteState: () => {}
};

const reducer = (state = initialState, action) => {
    switch(action.type) {
        case "UPDATE":
            return {
                myFavorites: JSON.parse(localStorage.getItem('myFavorites'))
            };
        default:
            return state;
    }
    return state;
}

const store = createStore(reducer);

export default store;
