import {
    useEffect,
    useState
} from 'react';
import {
    container as style_container,
    content as style_content,
    searchbox as style_searchbox,
    searchIcon as style_searchIcon
} from './index.module.css';
import {
    FontAwesomeIcon
} from '@fortawesome/react-fontawesome';
import Icon from '@material-ui/core/Icon';
import {
    SearchSharp
} from '@material-ui/icons/';

const ExpandableSearch = ({
    icon,
    ...props
}) => {
    const [screenWidth, setScreenWidth] = useState(0);
    const handleResize = (e) => {
        setScreenWidth(window.innerWidth);
    }
    useEffect(() => {
        setScreenWidth(window.innerWidth);
        window.addEventListener("resize", handleResize);
        return () => {
            window.addEventListener("resize", handleResize);
        }
    }, []);
    return <div
        className={style_container}
    >
       {
            screenWidth > 750 ?
                <div
                    className={style_content}
                >
                    <input
                        type="text"
                        className={style_searchbox}
                        {...props}
                    />
                    {
                        icon ?
                            <FontAwesomeIcon
                                {...icon}
                            />
                        :
                            null
                    }
                </div>
            :
                null
       }
        <Icon
            color={isOpenMobileMenu ? "primary" : "dark"}
            component={SearchSharp}
            className={style_searchIcon}
            style={{
                fontSize: 30
            }}
        />
    </div>
};
export default ExpandableSearch;
