const Search = ({

}) => {
    const [path, setPath] = useState("/");
    useEffect(() => {
        setPath(window.location.pathname);
        const searchText = window.location.search.replace("?s=", "");
    }, []);
    useEffect(() => {
        setPath(window.location.pathname);
    });
    return <Layout>
        <Breadcrumbs
