import Link from 'next/link';
import {
    container as style_container
} from './index.module.css';

const _child = ({
    item,
    index
}) => {
    return <div>
        {item.title}
        {
            data.length !== index+1 ?
                <span
                    style={{
                        marginLeft: 8
                    }}
                >
                    /
                </span>
            :
                null
        }
    </div>
}

const Breadcrumbs = ({
    data,
    path
}) => {
    return <div
        className={style_container}
    >
        {
            data.map((item, index) => {
                if(item.url) {
                    return <Link
                        key={"breadcrumb"+index}
                        href={item.url}
                        style={{
                            color: path === item.url ? "goldenrod" : "black"
                        }}
                    >
                        {_child({
                            item,
                            index
                        })}
                    </Link>
                } else {
                    return _child({
                        item,
                        index
                    })
                }
            })
        }
    </div>
};
export default Breadcrumbs;
