import {
  useEffect,
  useState
} from 'react';
import Head from 'next/head';

import api from '../server';

import {
  API_KEY,
  HOST
} from '../constants';

import style from './stylesheet';

export default function Home() {
  const [films, setFilms] = useState([]);
  const [err, setErr] = useState(null);
  useEffect(() => {
    const parameters = {
      "apikey": API_KEY,
      "s": "Yor"
    };
    const query = new URLSearchParams(parameters);
    api({
      url: HOST + "?" + query
    })
    .then((res) => {
      if(res.Response === "True") {
        setFilms(res.Search);
      } else {
        setErr({
          message: res["Error"]
        });
      }
    })
    .catch((err) => {
      alert(err.message);
    });
  }, []);
  return (
    <div className="container">
      <Head>
        <title>Create Next App</title>
        <link rel="icon" href="/favicon.ico" />
      </Head>

      <main>
        {
          films && films.length ?
            films.map((film) => {
              return <div
                className="container"
              >
                {film.title}
              </div>
            })
          :
            err ? err.message : "Yükleniyor..."
        }
      </main>

      <footer>

      </footer>

      <style jsx>{style}</style>
    </div>
  )
}
